import 'dart:io';

import 'package:flutter_dotenv/flutter_dotenv.dart';
import 'package:gtd_utils/constants/app_const.dart';
import 'package:gtd_utils/data/cache_helper/cache_helper.dart';
import 'package:gtd_utils/data/configuration/gtd_app_config.dart';

// ignore: constant_identifier_names
enum GTDEnvType { BannerAPI, CMSAPI, B2CAPI, AgentAPI, VIBAPI }

class GtdEnvironment {
  GTDEnvType env;
  late String baseUrl;
  late String platformPath;
  late Map<String, String> headers;
  GtdEnvironment({this.env = GTDEnvType.B2CAPI}) {
    
    if (AppConst.shared.appScheme.appSupplier == GtdAppSupplier.vib) {
      platformPath = dotenv.get("API_VIB_PATH", fallback: "vib");
    } else {
      String platform = Platform.operatingSystem;
      switch (platform) {
        case "ios":
          platformPath = dotenv.get("API_IOS_PATH", fallback: "b2c-web");
          break;
        case "android":
          platformPath = dotenv.get("API_ANDROID_PATH", fallback: "b2c-web");
          break;
        default:
          platformPath = dotenv.get("API_WEB_PATH", fallback: "b2c-web");
          break;
      }
    }

    switch (env) {
      case GTDEnvType.B2CAPI:
        baseUrl = dotenv.get("API_URL", fallback: "uat-api.gotadi.com");
        headers = {
          'Content-type': 'application/json',
          'Accept': 'application/json',
          // 'Authorization' : 'Bearer eyJhbGciOiJIUzUxMiJ9..F-FSdfF2Oehzhg5up-O0SZwMsRQ2YXlT7UDQ4ICAy6BN9FVdljbzxspknAIJZisq17_9_-w8YbypBSGALCV47A'
          // 'Authorization':
          //     'Bearer eyJhbGciOiJIUzUxMiJ9..RQBY_vIttLCXsWzktZnqq19H9FFDb-jCH4Q8hPWCuA-323FeBdrDehKyH7-NN-7BSNeZeb_IT-yCiFn5MtoTOA'
          'Authorization': CacheHelper.shared.getCachedAppToken()
        };
        break;
      default:
        baseUrl = "mobile-api.gotadi.com";
    }
  }
}
